import React from 'react';
import {Card,CardMedia,CardContent, CardActions,Typography, IconButton, Button, Modal} from '@material-ui/core';
import {AddShoppingCart} from '@material-ui/icons';
import {Link} from 'react-router-dom';
import ContactMailOutlinedIcon from '@material-ui/icons/ContactMailOutlined';
import useStyles from './ProductStyles';
import { makeStyles } from '@material-ui/core/styles';
function Product({product, onAddToCart}) {
    function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
    }
    const useStyles = makeStyles((theme) => ({
    paper: {
        position: 'absolute',
        width: 400,
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #000',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
    }));
    const [modalStyle] = React.useState(getModalStyle);
    const [open, setOpen] = React.useState(false);
    const classes = useStyles();
    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <Card className={classes.root}>
            <CardMedia className={classes.media} image={product.media.source} title={product.name}/>
            <CardContent>
                <div className={classes.CardContent}>
                    <Typography variant='h5' gutterBottom>
                        {product.name}
                    </Typography>
                    <Typography variant='h5' gutterBottom>
                        {product.price.formatted_with_symbol}
                    </Typography>
                    <Typography dangerouslySetInnerHTML={{__html: product.description}} variant='body2' color="textSecondary" />

                </div>
            </CardContent>
            <CardActions disableSpacing className={classes.CardActions}>
                <IconButton arial-label='Add to Cart' onClick={() => onAddToCart(product.id, 1)}>
                    <AddShoppingCart />
                </IconButton>
                <IconButton>
                    <ContactMailOutlinedIcon />
                </IconButton>
                <Button onClick={handleOpen} style={{ textDecoration: 'none',marginLeft: "auto" }} size="middle" type = "button" variant="contained" color="primary">See Details</Button>
                <Modal
                        open={open}
                        onClose={handleClose}
                        aria-labelledby="simple-modal-title"
                        aria-describedby="simple-modal-description"
                        >
                <CardContent>
                <div className={classes.CardContent}>
                    <Typography variant='h5' gutterBottom>
                        {product.name}
                    </Typography>
                    <Typography variant='h5' gutterBottom>
                        {product.price.formatted_with_symbol}
                    </Typography>
                    <Typography dangerouslySetInnerHTML={{__html: product.description}} variant='body2' color="textSecondary" />

                </div>
            </CardContent>
                </Modal>
            </CardActions>
        </Card>
    )
}

export default Product
