import React from 'react';
import {Container, Typography, Button, Grid} from '@material-ui/core';
import useStyles from "./CartStyle";
const Cart = () => {
    const classes = useStyles();
    const EmptyCart = ({FilledCart}) => (
        <Typography variant="subtitle1">
            You have no items in your cart, shart adding some!
        </Typography>
    );
    const FilledCart = () => (
        <div>
            <Grid container spacing={3}>
                {cart.line_items.map((item) => {
                    <Grid item xs={12} sm={4} key={item.id}> 
                        <div>{item.name}</div>
                    </Grid>

                })}
            </Grid>
            <div className={classes.cardDetails}>
                <Typography variant="h4">
                    Subtotal:{cart.subtotal.formatted_with_symbol}
                </Typography>
                <div>
                    <Button className={classes.emptyButton} size="large" type = "button" variant="contained" color="secondary">Empty Cart</Button>
                    <Button className={classes.emptyButton} size="large" type = "button" variant="contained" color="primary">Checkout</Button>

                </div>
            </div>

        </div>

    );
    if (!cart.line_items) return "loading....";
    return (
        <Container>
            <div className={classes.toolbar} />
            <Typography className={classes.title} variant='h3'>
                Your Shopping Cart
            </Typography>
            {!cart.line_items.length? <EmptyCart /> : <FilledCart />}
        </Container>
    )

}
export default Cart;
